import { useState } from "react";
import { Link } from "react-router-dom";
import "./newClient.css";

function NewClient(){
    const [cnp, setCNP] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [dob, setDob] = useState("");
  const [gender, setGender] = useState("");
  const [income, setIncome] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const[category, setCategory] = useState("");
  const[categories, setCategories] = useState([]);
  const [policyTypes, setPolicyTypes] = useState([]);
  const [price, setPrice] = useState("");
  const [maxClaimAmount, setMaxClaimAmount] = useState("");
  const[idPolicy, setIdPolicy] = useState("");

  const generatePassword = () => {
    const randomPassword = {cnp}.cnp + "Parola0"
    setPassword(randomPassword);
  };
  const saveNewClient = (e) => {
    e.preventDefault();
    const user = {firstName, lastName, email, password}
    const client = {cnp, dob, gender, income, phoneNumber, user} 
    const policyType={idPolicy}   
    const policy = {endDate, startDate, maxClaimAmount, price, client}
    const clientAndUser = {client, policy, policyType}


}
    return(
        <form>
        <div className="row">
          <div class="column">
          <div className="form-headings">Personal Details</div>
            <label className="form-label">CNP:</label>
            <input
              type="text"
              placeholder="Enter clients CNP"
              name="cnp"
              className="form-control"
              value={cnp}
              onChange={(e) => setCNP(e.target.value)}
            ></input>
            <label className="form-label">First name:</label>
            <input
              type="text"
              placeholder="Enter clients first name"
              name="firstName"
              className="form-control"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
            ></input>
            <label className="form-label">Last name:</label>
            <input
              type="text"
              placeholder="Enter clients last name"
              name="lastName"
              className="form-control"
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
            ></input>
            <label className="form-label">Date of birth:</label>
            <input
              type="date"
              placeholder="Enter clients date of birth"
              name="dob"
              className="form-control"
              value={dob}
              onChange={(e) => setDob(e.target.value)}
            ></input>
            <label className="form-label">Gender:</label>
            <div className="radios">
                <input type="radio" name="gender" value="F" onChange={(e) => setGender(e.target.value)}/>
                <label>Female</label>
                <input type="radio" name="gender" value="M" onChange={(e) => setGender(e.target.value)}/>
                <label>Male</label> 
            </div>
            <label className="form-label">Income:</label>
            <input
              type="number"
              placeholder="Enter clients income"
              name="income"
              className="form-control"
              value={income}
              onChange={(e) => setIncome(e.target.value)}
            ></input>
            <label className="form-label">Phone number:</label>
            <input
              type="number"
              placeholder="Enter clients phone number"
              name="phoneNumber"
              className="form-control"
              value={phoneNumber}
              onChange={(e) => setPhoneNumber(e.target.value)}
            ></input>
            <div className="form-headings">Account Details</div>
            <label className="form-label">Email:</label>
            <input
              type="email"
              placeholder="Enter clients email"
              name="email"
              className="form-control"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            ></input>
            <label className="form-label">Password:</label>
              <div className="input-group mb-2">
                <input readOnly = {true} 
                  type="text" 
                  className="form-control" 
                  placeholder="password" 
                  name="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  />
                <div class="input-group-append">
                  <button disabled={!cnp}
                    className="btn btn-dark"
                    value="Generate Password"
                    onClick={generatePassword} type="button">Button
                  </button>
                </div>
            </div>
          </div>
          <div class="column" >
          <div className="form-headings">Policy</div>
            <label className="form-label">Insurance Category:</label>
            <select
                name="category"
                className="form-control"
                value={category}
                //onChange={(e) => setCategory(e.target.value)}>
                //onChange={handleCategoryChange}
                ><option defaultValue="selected">
                ---SELECT CATEGORY---
                </option>
              {categories.map(categ=> (
                <option key={categ.id} value={categ.id}>
                    {categ.title}
                </option>
                ))}
            </select> 
            <br></br>
            <label className="form-label">Policy Type:</label>
            <select
                name="policyType"
                className="form-control"
                value= {idPolicy}
                onChange={(e) => {setIdPolicy(e.target.value)}}>
                <option defaultValue="selected">
                ---SELECT POLICY TYPE---
                </option>
              {policyTypes.map(type=>  (
                <option key={type.idPolicy} value={type.idPolicy}>
                    {type.title}
                </option> 
                ))}
            </select> 
            <br></br>
            <label className="form-label">Date Start:</label>
            <input
              type="date"
              placeholder="Enter policy date start"
              name="startDate"
              className="form-control"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
            ></input>
          <label className="form-label">Date End:</label>
            <input
              type="date"
              placeholder="Enter policies date end"
              name="dateEnd"
              className="form-control"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
            ></input>
              <label className="form-label">Price:</label>
            <input
              type="number"
              name="price"
              className="form-control"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
            ></input>
              <label className="form-label">Maximum claim amount:</label>
            <input
              type="number"
              name="maxClaimAmount"
              className="form-control"
              value={maxClaimAmount}
              onChange={(e) => setMaxClaimAmount(e.target.value)}
            ></input> 
            <div id="buttons">
              <button id="save" className="btn btn-success" onClick={(e) => saveNewClient(e)}>Save</button>
              <Link to = "/manage-clients" id="cancel" className="btn btn-danger">Cancel</Link>
            </div>
          </div>
        </div>
        </form>
    );
}
export default NewClient;
